package 滑动窗口;

/**
 * Created by ouyang on 2020/7/15.
 */
public class 最小覆盖子串_hard_76 {
    public static void main(String[] args) {
        String ans = minWindow("abc", "ac");
        System.out.println("ans=" + ans);

        System.out.println("#############################");
        ans = minWindow("ADOBECODEBANC", "ABC");
        System.out.println("ans=" + ans);


        System.out.println("#############################");
        ans = minWindow("",
                "");
        System.out.println("ans=" + ans);
    }

    public static String minWindow(String s, String t) {
        int left = 0;
        int right = 0;

        // 最终目标
        int[] need = new int[128];
        int needCnt = 0;
        for (int i = 0; i < t.length(); i++) {
            need[t.charAt(i)]++;
            needCnt++;
        }

        // 记录动态的窗口值
        int[] window = new int[128];

        int resLeft = -1;
        int resRight = -1;
        int minLen = Integer.MAX_VALUE;

        int count = 0;
        while (right < s.length()) {
            // 窗口不满足就一直伸
            char rightChar = s.charAt(right);
            window[rightChar]++;
            if (need[rightChar] > 0 && window[rightChar] <= need[rightChar]) {
                count++;
            }

            if (count < needCnt) {
                right++;
            }
            // 如果窗口符合need则进来收缩
            else if (count == needCnt) {

                // 收缩left直到窗口再次不满足
                while (count == needCnt) {
                    // 答案记录
                    if (right - left < minLen) {
                        minLen = right - left;
                        resLeft = left;
                        resRight = right;
                    }

                    // 收缩left
                    char leftCh = s.charAt(left);
                    if (need[leftCh] > 0 && need[leftCh] >= window[leftCh]) {
                        count--;
                    }
                    window[leftCh]--;
                    left++;
                }
                right++;
            }
        }

        if (resRight == -1) return "";
        return s.substring(resLeft, resRight + 1);
    }

}
